import axios from 'axios'

const USER_API_URL = 'http://localhost:9080/api/v1'

class PessoaDataService {

    retrieveAllPessoas() {
        return axios.get(`${USER_API_URL}/pessoa`);
    }

    retrievePessoa(id) {
        return axios.get(`${USER_API_URL}/pessoa/${id}`);
    }

    deletePessoa(id) {
        return axios.delete(`${USER_API_URL}/pessoa/${id}`);
    }

    updatePessoa(id, pessoa) {
        return axios.put(`${USER_API_URL}/pessoa/${id}`,pessoa);
    }
    createPessoa(pessoa) {
        return axios.post(`${USER_API_URL}/pessoa`,pessoa);
    }
  }
export default new PessoaDataService()
