import { createWebHistory, createRouter } from "vue-router";

const routes =  [
    {
        path: "/",
        name: "Pessoas",
        component: () => import("./components/Pessoas"),
    },
    {
        path: "/pessoa/:id",
        name: "Pessoa",
        component: () => import("./components/Pessoa"),
    },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
